    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        String pwd = String.valueOf(txtPassword.getPassword());
        String usuario = txtUsuario.getText().trim(); 
        if(usuario.length() > 1 && pwd.trim().length() > 1 ) {
            Encriptacion ec = new Encriptacion();
            String pwdEncrypt = ec.encriptar(pwd);
            AuthController auth = new AuthController();
            String cargo = auth.Login(usuario, pwdEncrypt); 
            if(cargo.matches("Administrador|Trabajador")) {
                role = cargo;
                Home2 home = new Home2();
                home.setVisible(true);
                this.setVisible(false);
                JOptionPane.showMessageDialog(null, "Bienvenido al sistema", "Success", JOptionPane.INFORMATION_MESSAGE);
            } else {
                JOptionPane.showMessageDialog(null, "Usuario y/o Contrase√±a incorrectos", "Error", JOptionPane.ERROR_MESSAGE);
            }
        } else {
            JOptionPane.showMessageDialog(null, "Verifique los campos", "Error", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_btnIngresarActionPerformed

